	/**
	 * Salva os mapeamentos de veículos em um arquivo
	 * 
	 * @param nomeArquivo		Nome do arquivo onde os veículos serão salvos
	 * @param pesquisadores		Lista de pesquisadores cujas publicações serão examinadas
	 */
	public void salvaVeiculos(String nomeArquivo, ListaPesquisadores pesquisadores) throws IOException
	{
		List<String> veiculos = new ArrayList<String>();
		
		for (Pesquisador p : pesquisadores)
			for (Publicacao pub : p.pegaPublicacoes())
				if (pub.getVeiculo().length() > 0)
					if (veiculos.indexOf(pub.getVeiculo()) == -1)
						veiculos.add(pub.getVeiculo());

	    Writer out = new OutputStreamWriter(new FileOutputStream(new File(nomeArquivo)), "UTF8");

	    for (String veiculo : veiculos)
	    	if (veiculo.length() > 0)
	    		out.write(veiculo + "\n");
	    
		out.close();
	}

	/**
	 * Aplica o mapeamento e gera uma lista de veículos com nomes únicos
	 */
	private List<String> geraListaVeiculosUnicos()
	{
		List<String> veiculos = new ArrayList<String>();
		
		for (int i = 0; i < DE_PARA_VEICULOS.length; i += 2)
		{
			String novoNome = DE_PARA_VEICULOS[i+1].trim();
			
			if (novoNome.length() > 0 && !veiculos.contains(novoNome))
				veiculos.add(novoNome);
		}

		return veiculos;
	}

	/**
	 * Verifica se um veículo é exceção na lista de veículos com nome muito curto
	 * 
	 * @param excecoes		Lista de veículos com nomes curtos aceitos
	 * @param nome			Nome do veículo desejado
	 */
	private boolean verificaExcecaoNomesPequenos(String[] excecoes, String nome)
	{
		for (int i = 0; i < excecoes.length; i++)
			if (nome.compareTo(excecoes[i]) == 0)
				return true;

		return false;
	}

	/**
	 * Lista os veículos com nome muito curto
	 * 
	 * @param veiculos		Lista de veículos com nomes convertidos pelo DE-PARA
	 */
	private void processaNomesPequenos(List<String> veiculos)
	{
		String[] EXCECOES =
		{
				"IEEE MICRO"
		};
		
		for (int i = 0; i < veiculos.size(); i++)
		{
			String nome = veiculos.get(i);
			
			if (nome.length() < 12)
				if (!verificaExcecaoNomesPequenos(EXCECOES, nome))
					System.out.println(nome);
		}
	}

	/**
	 * Verifica se um par de nomes de veículos representam uma exceção na comparação de distâncias
	 * 
	 * @param excecoes			Lista de exceções aceitas
	 * @param nomePrimeiro		Nome do primeiro veículo
	 * @param nomeSegundo		Nome do segundo veículo
	 */
	private boolean verificaExcecaoDistanciaLevenstein(String[] excecoes, String nomePrimeiro, String nomeSegundo)
	{
		for (int i = 0; i < excecoes.length; i += 2)
		{
			if (nomePrimeiro.compareTo(excecoes[i]) == 0 && nomeSegundo.compareTo(excecoes[i+1]) == 0)
				return true;
			
			if (nomePrimeiro.compareTo(excecoes[i+1]) == 0 && nomeSegundo.compareTo(excecoes[i]) == 0)
				return true;
		}

		return false;
	}

	/**
	 * Verifica se o nome de um veículo está a uma distância pequena de outro
	 * 
	 * @param veiculos		Lista de veículos com nomes convertidos pelo DE-PARA
	 */
	private void processaDistanciaLevenstein(List<String> veiculos)
	{
		String[] EXCECOES =
		{
				"FOUNDATIONS OF ASPECT-ORIENTED LANGUAGES", "FOUNDATIONS OF OBJECT-ORIENTED LANGUAGES",
				"IEEE SOFTWARE", "IET SOFTWARE"
		};
		
		DistanciaLevenstein dl = new DistanciaLevenstein();
		
		for (int i = 0; i < veiculos.size(); i++)
		{
			String nomePrimeiro = veiculos.get(i);
			
			for (int j = 0; j < veiculos.size(); j++)
			{
				if (i != j && nomePrimeiro.length() >= 12)
				{
					String nomeSegundo = veiculos.get(j);
					
					if (dl.calcula(nomePrimeiro, nomeSegundo) < 5)
						if (!verificaExcecaoDistanciaLevenstein(EXCECOES, nomePrimeiro, nomeSegundo))
							System.out.println(nomePrimeiro + " => " + nomeSegundo);
				}
			}
		}
	}

	/**
	 * Verifica se um veículo fica com nome igual a outro quando o prefixo INTERNATIONAL
	 * é adicionado ao seu nome
	 * 
	 * @param veiculos		Lista de veículos com nomes convertidos pelo DE-PARA
	 */
	private void processaPrefixoInternacional(List<String> veiculos)
	{
		for (int i = 0; i < veiculos.size(); i++)
		{
			String nome = veiculos.get(i);
			
			if (!nome.startsWith("International"))
				if (veiculos.contains("International " + nome))
					System.out.println(nome + " => " + "International " + nome);
		}
	}
	
	/**
	 * Verifica a consistência dos veículos onde um conjunto de pesquisadores publicou
	 * apresentando conferências e journals com nomes próximos entre si
	 * 
	 * @param pesquisadores		Lista de pesquisadores sendo analisados
	 */
	public void verificaConsistenciaVeiculos(ListaPesquisadores pesquisadores)
	{
		List<String> veiculos = geraListaVeiculosUnicos();
		processaPrefixoInternacional(veiculos);
		processaDistanciaLevenstein(veiculos);
		processaNomesPequenos(veiculos);
	}

	/**
	 * Converte o nome de um veículo pela tabela DE-PARA
	 */
	public String converteVeiculo(String veiculoOriginal)
	{
		veiculoOriginal = veiculoOriginal.trim();
		
		for (int i = 0; i < DE_PARA_VEICULOS.length; i += 2)
		{
			String nome = DE_PARA_VEICULOS[i].trim();
			
			if (veiculoOriginal.compareToIgnoreCase(nome) == 0)
				return DE_PARA_VEICULOS[i+1].trim();
		}
		
		return "";
	}
